import Dialogs from './Dialogs'
import { sendNewMessage, updateTextMessage } from '../redux/dialogsReducer';
import { connect } from 'react-redux';
import { withAuthRedirect } from '../../hoc/withAuthRedirect';

const mapStateToProps = (state) => {
    return {
        dialogsPage: state.dialogsPage,
        isAuth: state.auth.isAuth
    }
}

let AuthRedirectComponent = withAuthRedirect(DialogsContainer);

const DialogsContainer = connect(mapStateToProps, {sendNewMessage, updateTextMessage})(Dialogs);

export default DialogsContainer;
