const SET_USER_DATA = 'SET-USER-DATA';

let initialState = {
    userId: null,
    emeail: null,
    login: null,
    isFetch: false
};

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.data,
            };
        default:
            return state;
    }
}

export const setAuthUserData = (data) => ({type: SET_USER_DATA, data: {userId, email, login}})

export default authReducer;
