import { authAPI } from "../../API/authAPI";

const SET_USER_DATA = 'SET-USER-DATA';

let initialState = {
    userId: null,
    emeail: null,
    login: null,
    isAuth: false
};

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.pa,
                isAuth: true,
            };
        default:
            return state;
    }
}

export const setAuthUserData = (userId, email, login) => ({type: SET_USER_DATA, payload: {userId, email, login}})

export const setUserData = () => (dispatch) => {
        authAPI.getUserData()
        .then(data => {
            if (data.resultCode === 0) {
                let {id, email, login} = data.data;
                dispatch(setAuthUserData(id, email, login));
            }
        });
}

export const login = (email, password, rememberMe, isAuth) => (dispatch) => {
        authAPI.login()
        .then(data => {
            if (data.resultCode === 0) {
                dispatch(setUserData(email, password, rememberMe, true));
            }
        });
}

export const logout = () => (dispatch) => {
        authAPI.logout()
        .then(data => {
            if (data.resultCode === 0) {
                dispatch(setUserData(email, password, rememberMe, true));
            }
        });
}


export default authReducer;
