import React from 'react';
import s from './Dialogs.module.css';
import Message from './Message/Message.jsx';
import DialogItem from './DialogItem/DialogItem.jsx';
import { sendNewMessageCreator, updateTextMessageCreator } from '../redux/dialogsReducer';

const Dialogs = (props) => {
    let dialogsElements = props.state.dialogs.map(d => <DialogItem name={d.name} id={d.id} />);
    let messagesElements = props.state.messages.map(m => <Message text={m.text} id={m.id} />);
    let newMessageBody = props.state.newMessageText;

    let sendMessage = () => {
        props.dispatch(sendNewMessageCreator());
    };

    let newMessageText = (e) => {
        let text = e.target.value;
        props.dispatch(updateTextMessageCreator(text));
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                {messagesElements}
                <textarea value={newMessageBody} placeholder='Enter your mesagge text' onChange={newMessageText}></textarea>
                <button onClick={sendMessage}>Send message</button>
            </div>
        </div>
    )
}

export default Dialogs;
