import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post.jsx';
import { Field, reduxForm } from 'redux-form';


const AddPostForm = ({handleSubmit}) => {
    return (
        <form onSubmit={handleSubmit}>
            <div>
                <Field component={'textarea'} name={'addNewPost'} placeholder={'Enter your post'} />
            </div>
            <div><button>Add post</button></div>
        </form>
    )
}

const AddPostReduxForm = reduxForm({ form: 'profileAddPost' })(AddPostForm);

const MyPosts = (props) => {

    let postsElements = props.posts.map(p => <Post message={p.message} likeCount={p.likeCount} id={p.id} />);

    let newPost = () => {
        props.addPost();
    };

    let onPostChange = (e) => {
        let text = e.target.value;
        props.updatePostText(text);
    }

    const onSubmit = (values) => {
        props.addPost(values.addNewPost);
    }

    return (
        <div className={s.myPosts}>
            <h3>My posts</h3>
            <hr />
            <div>
                <AddPostReduxForm  />
            </div>
            <div>
                {postsElements}
            </div>
        </div>
    )

}

export default MyPosts;
