    let postsElements = props.postsArray.map ( p => <Post message = {p.message} likeCount = {p.likeCount} id = {p.id} />);

    let newPostElement = React.createRef

    return (
        <div className={s.myPosts}>
            <h3>My posts</h3>
            <hr />
            <div>
                <textarea></textarea>
                <button>Add post</button>
            </div>
            <div>
                {postsElements}
            </div>
        </div>
    )
}

export default MyPosts;
