const SET_USER_DATA = 'SET-USER-DATA';

let initialState = {
    userId: null,
    emeail: null,
    login: null,
    isFetch: false
};

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state
            }
        default:
            return state;
    }
}

export const sendNewMessage = () => ({ type: SEND_MESSAGE });
export const updateTextMessage = (text) => ({ type: UPDATE_MESSAGE_TEXT, body: text });

export default authReducer;
