import React from 'react';
import s from './Dialogs.module.css';
import Message from './Message/Message.jsx';
import DialogItem from './DialogItem/DialogItem.jsx';
import { Redirect } from 'react-router-dom';

const Dialogs = (props) => {
    let dialogsElements = props.dialogsPage.dialogs.map(d => <DialogItem name={d.name} id={d.id} key = {d.id}/>);
    let messagesElements = props.dialogsPage.messages.map(m => <Message text={m.text} id={m.id} key = {m.id}/>);
    let newMessageBody = props.dialogsPage.newMessageText;

    let sendMessage = () => {
        props.sendMessage();
    };

    let newMessageText = (e) => {
        let text = e.target.value;
        props.newMessageText(text);
    }

   

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                {messagesElements}
                <textarea value={newMessageBody} placeholder='Enter your mesagge text' onChange={newMessageText}></textarea>
                <button onClick={sendMessage}>Send message</button>
            </div>
        </div>
    )
}

export default Dialogs;
