const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';

let initialState = {
    users: [
        // { id: 1, followed: true, location: {city: 'Smolensk', country: 'Russia'}, fullName: 'Ulya', avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQrnHsHJ875K2DcaOlSvGPmv86g5kdZiiAXQQ&usqp=CAU' },
        // { id: 2, followed: true, location: {city: 'Smolensk', country: 'Russia'}, fullName: 'Mama', avatar: 'https://sun9-55.userapi.com/impf/0rsrpoqwHtmb90vTX-QCCsE1Hrj9IuCEF75FjQ/UxSgfyJ0PJM.jpg?size=200x0&quality=90&crop=0,1,742,742&sign=55c05187279ddb98125702177a68dc04&ava=1' },
        // { id: 3, followed: false, location: {city: 'Dvor', country: 'Smolensk'}, fullName: 'Ptichky', avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcStQFXMUCoVnK8AyFWMv-xZw8oeweJ_nvOdjA&usqp=CAU' },
        // { id: 4, followed: true, location: {city: 'Dvor', country: 'Smolensk'}, fullName: 'Tejta', avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTBJRVH7DC3pw_t57B9Ccs8Ilt_DMHyFvx2VQ&usqp=CAU' },
    ]
};

const usersReducer = (state = initialState, action) => {


    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }

        case SET_USERS:
            return {
                ...state, 
                users: [...state.users, ...action.users]
            }
            
        default:
            return state;
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId });
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId });
export const setUsersAC = (users) => ({ type: SET_USERS, users });

export default usersReducer;
